import React, { useState } from "react";
import { FaCamera } from "react-icons/fa";

type Setting = {
    name: string;
    value: string;
};

type SettingProps = {
    settings: Setting[];
    onSave: (settings: Setting[]) => void;
};

const SettingPage: React.FC = ({ }) => {
    //   const [updatedSettings, setUpdatedSettings] = useState(settings);
    const [updatedSettings, setUpdatedSettings] = useState(false);
    const [avatar_image, setAvatar_image] = useState("");
    const [nickname, setNickname] = useState("John Doe");

    const handleSave = () => {
        // onSave(updatedSettings);
    };

    const handleSettingChange = (index: number, event: React.ChangeEvent<HTMLInputElement>) => {
        // const newSettings = [...updatedSettings];
        // newSettings[index].value = event.target.value;
        // setUpdatedSettings(newSettings);
        setUpdatedSettings(true);
    };

    const handleProfileImageChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        // const file = event.target.files?.[0];
        // if (file) {
        //     const reader = new FileReader();
        //     reader.onloadend = () => {
        //         setProfileImage(reader.result as string);
        //     };
        //     reader.readAsDataURL(file);
        // }
    };


    return (
        <div
            style={{
                backgroundColor: "rgba(132, 0, 0, 0.9)",
                height: "100vh",
                backgroundSize: "cover",
                backgroundRepeat: "no-repeat",
                backgroundPosition: "center",
                display: 'flex',
                padding: '0 20px',
                flexDirection: 'column',
                justifyContent: 'space-between',
            }}>
            <h1 style={{
                fontFamily: "Times New Roman",
                marginTop: 15,
                fontSize: 48
            }}>Settings</h1>
            <div
                style={{
                    display: "flex",
                    alignItems: "center"
                }}>
                {avatar_image ? (
                    <img src={avatar_image} alt="Profile" style={{ width: "150px", height: "150px", borderRadius: "50%" }} />
                ) : (
                    <div
                        style={{
                            marginLeft: "20px",
                            marginTop: "-250px",
                        }}>
                        <label htmlFor="profile-image-input">
                            <div
                                style={{
                                    width: "150px",
                                    height: "150px",
                                    borderRadius: "50%",
                                    backgroundColor: "#ccc",
                                    display: "flex",
                                    justifyContent: "center",
                                    alignItems: "center",
                                    cursor: "pointer",
                                    textAlign: "center",
                                }}
                            >
                                <FaCamera size={50} color="#fff" />
                            </div>
                            <span
                                style={{
                                    marginLeft: "10px",
                                    fontFamily: "Times New Roman",
                                    fontSize: "16px",
                                    textAlign: "justify",
                                }}>
                                Change profile image
                            </span>
                        </label>
                        <input
                            type="file"
                            accept="image/*"
                            id="profile-image-input"
                            onChange={handleProfileImageChange}
                            style={{ display: "none" }}
                        />
                    </div>
                )}
                <div
                    style={{
                        marginLeft: "50px",
                        marginTop: "-350px",
                    }}>
                    <label htmlFor="profileName">Nickname: </label>
                    <input
                        type="text"
                        id="nickname"
                        value={nickname}
                        onChange={(e) => setNickname(e.target.value)}
                    />
                </div>
                <></>
                <label htmlFor="username">User Name: </label>
                <input
                    type="text"
                    id="nickname"
                    value={nickname}
                    onChange={(e) => setNickname(e.target.value)}
                />
            </div>
            {/* {updatedSettings.map((setting, index) => (
                <div key={setting.name}>
                    <label 
                    htmlFor={setting.name}>{setting.name}:</label>
                    <input
                        type="text"
                        id={setting.name}
                        value={setting.value}
                        onChange={(e) => handleSettingChange(index, e)}
                    />
                </div>
            ))} */}
            {/* {updatedSettings.map((setting, index) => (
                <div key={index}> */}
            <label>
                {/* {setting.name}
                        <input type="text" ></input>
                        value={setting.value} onChange={(event) => handleSettingChange(index, event)} /> */}
            </label>
            {/* </div>))} */}
            <button onClick={handleSave}>Save</button>
        </div>
    );
};

export default SettingPage;
