import React, { useState } from "react";
import { FaCamera } from "react-icons/fa";
import { NickUserName } from "./NickUserName";
import { SPAchievements } from "./SPAchievements";

type Setting = {
    name: string;
    value: string;
};

type SettingProps = {
    settings: Setting[];
    onSave: (settings: Setting[]) => void;
};

const SettingPage: React.FC = ({ }) => {
    //   const [updatedSettings, setUpdatedSettings] = useState(settings);
    const [updatedSettings, setUpdatedSettings] = useState(false);
    const [avatar_image, setAvatar_image] = useState("");


    const handleSave = () => {
        // onSave(updatedSettings);
    };

    const handleSettingChange = (index: number, event: React.ChangeEvent<HTMLInputElement>) => {
        // const newSettings = [...updatedSettings];
        // newSettings[index].value = event.target.value;
        // setUpdatedSettings(newSettings);
        setUpdatedSettings(true);
    };

    const handleProfileImageChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        // const file = event.target.files?.[0];
        // if (file) {
        //     const reader = new FileReader();
        //     reader.onloadend = () => {
        //         setProfileImage(reader.result as string);
        //     };
        //     reader.readAsDataURL(file);
        // }
    };


    return (
        <div
            style={{
                backgroundSize: "cover",
                backgroundRepeat: "no-repeat",
                backgroundPosition: "center",
                display: 'flex',
                padding: '0 20px',
                flexDirection: 'column',
                justifyContent: 'space-between',
                boxSizing: "border-box",

                position: "relative",
                height: "896px",

                background: "#B12D2D",
                border: "1px solid #661212",
                borderRadius: "12px",
            }}>
            <div
                style={{
                    display: "flex",
                    alignItems: "center"
                }}>
                {avatar_image ? (
                    <img src={avatar_image} alt="Profile" style={{ width: "150px", height: "150px", borderRadius: "50%" }} />
                ) : (
                    <div>
                        <label htmlFor="profile-image-input">
                            <div
                                style={{
                                    width: "200px",
                                    height: "200px",
                                    // left: "150px",
                                    // top: "62px",
                                    borderRadius: "50%",
                                    backgroundColor: "#ccc",
                                    display: "flex",
                                    justifyContent: "center",
                                    alignItems: "center",
                                    cursor: "pointer",
                                    textAlign: "center",
                                    transform: 'translate(20%, 30%)',
                                }}
                            >
                                <FaCamera size={60} color="#fff" />
                            </div>
                            <div style={{
                                cursor: "pointer",
                                transform: 'translate(10%, 700%)',
                                textAlign: "justify",
                                position: "absolute",
                                width: "336px",
                                height: "32px",
                                left: "26px",
                                top: "48.37px",
                                fontFamily: 'Figma Hand',
                                fontStyle: "normal",
                                fontWeight: 400,
                                fontSize: "21px",
                                lineHeight: "150%",
                                color: "#E6E6E6",
                            }} >
                                Change profile image
                            </div>
                        </label>
                        <input
                            type="file"
                            accept="image/*"
                            id="profile-image-input"
                            onChange={handleProfileImageChange}
                            style={{ display: "none" }}
                        />
                    </div>
                )
                }
                <NickUserName/>
            </div >
            <SPAchievements/>

            {/* {updatedSettings.map((setting, index) => (
                <div key={setting.name}>
                    <label 
                    htmlFor={setting.name}>{setting.name}:</label>
                    <input
                        type="text"
                        id={setting.name}
                        value={setting.value}
                        onChange={(e) => handleSettingChange(index, e)}
                    />
                </div>
            ))} */}
            {/* {updatedSettings.map((setting, index) => (
                <div key={index}> */}
            <label>
                {/* {setting.name}
                        <input type="text" ></input>
                        value={setting.value} onChange={(event) => handleSettingChange(index, event)} /> */}
            </label>
            {/* </div>))} */}
            <button
                style={{ fontSize: "30px" }}
                onClick={handleSave}>
                Save
            </button>
        </div >
    );
};

export default SettingPage;
