import React, { useRef } from 'react'
import { ChangeProfileImage } from './ChangeProfileImage'

export const AchievementShowMore = () => {
  const smallBgRef = useRef<HTMLDivElement>(null);

  const scrollLeft = () => {
    if (smallBgRef.current) {
      smallBgRef.current.scrollBy({
        left: 100,
        behavior: "smooth",
      });
    }
  };

  const scrollRight = () => {
    if (smallBgRef.current) {
      smallBgRef.current.scrollBy({
        left: 100,
        behavior: "smooth",
      });
    }
  };

  return (
    <div
      style={{
        position: "absolute",
        top: 0,
        right: 0,
        left: 0,
        backgroundSize: "cover",
        backgroundRepeat: "no-repeat",
        backgroundPosition: "center",
        display: 'flex',
        padding: '0 20px',
        flexDirection: 'column',
        justifyContent: 'space-between',
        boxSizing: "border-box",
        height: "896px",
        background: "#B12D2D",
      }}>
      <ChangeProfileImage />
      <h1 style={{
        backgroundColor: "#ccc",
        padding: "20px 46px",
        borderRadius: "5px",
        position: "absolute",
        top: "40%",
        left: "26%",
        fontSize: "20px",
        transform: "translate(-50%, -50%)",
      }}>
        Rank name
      </h1>
      <div
        style={{
          backgroundColor: "#ccc",
          borderRadius: "2%",
          position: "absolute",
          width: "400px",
          height: "600px",
          left: "45%",
          top: "30%",
          display: "flex",
          flexDirection: "column",
          // padding: "20px 46px",

          // justifyContent: "center", 
          alignItems: "center"
        }}
      >
        <div
          style={{
            backgroundColor: "grey",
            height: "100px",
            overflowY: "scroll",
            whiteSpace: "nowrap",
          }}
          ref={smallBgRef}
        >
          {/* <div style={{ display: "inline-block", width: "200px", height: "100px", backgroundColor: "red" }}></div>
          <div style={{ display: "inline-block", width: "200px", height: "100px", backgroundColor: "blue" }}></div>
          <div style={{ display: "inline-block", width: "200px", height: "100px", backgroundColor: "green" }}></div>
          <div style={{ display: "inline-block", width: "200px", height: "100px", backgroundColor: "yellow" }}></div>
          <div style={{ display: "inline-block", width: "200px", height: "100px", backgroundColor: "orange" }}></div> */}
          <button onClick={scrollLeft}>{"<"}</button>
          <button onClick={scrollRight}>{">"}</button>
        </div>
      </div >
    </div>
  );
};
