import React, { useState } from "react";

type Setting = {
  name: string;
  value: string;
};

type SettingProps = {
  settings: Setting[];
  onSave: (settings: Setting[]) => void;
};

const SettingPage: React.FC = ({}) => {
//   const [updatedSettings, setUpdatedSettings] = useState(settings);
  const [updatedSettings, setUpdatedSettings] = useState(false);
  const [profileImage, setProfileImage] = useState("");


  const handleSave = () => {
    // onSave(updatedSettings);
  };

  const handleSettingChange = (index: number, event: React.ChangeEvent<HTMLInputElement>) => {
    // const newSettings = [...updatedSettings];
    // newSettings[index].value = event.target.value;
    // setUpdatedSettings(newSettings);
    setUpdatedSettings(true);
  };

  const handleProfileImageChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setProfileImage(reader.result as string);
      };
      reader.readAsDataURL(file);
    }
  };


  return (
    <div>
      <h1>Settings</h1>
      {profileImage && <img src={profileImage} alt="Profile" />}
      {/* {updatedSettings.map((setting, index) => ( */}
      <div>
        <label>
          Change profile image
          <input type="file" accept="image/*" onChange={handleProfileImageChange} />
        </label>
      </div>
      {/* {updatedSettings.map((setting, index) => ( */}
        <div key={index}>
          <label>
            {setting.name}
            <input type="text" value={setting.value} onChange={(event) => handleSettingChange(index, event)} />
          </label>
        </div>
      ))}
        <div 
        // key={index}
        >
          <label>
            {/* // {setting.name} */}
            <input type="text" 
            // value={setting.value} 
            // onChange={(event)
            //  => handleSettingChange(index, event)} 
            />
          </label>
        </div>
      {/* )) */}
      {/* } */}
      <button onClick={handleSave}>Save</button>
    </div>
  );
};

export default SettingPage;
