import React, { useRef } from 'react'
import { ChangeProfileImage } from './ChangeProfileImage'
import Oval from './OvalShaped';
import Square from './SquareShaped';
import styled from 'styled-components';

const ScrollableContainer = styled.div`
  height: 896px;
  overflow-y: scroll;
  padding-right: 40px;

  &::-webkit-scrollbar {
    width: 8px;
  }

  &::-webkit-scrollbar-track {
    background: #f1f1f1;
    border-radius: 5px;
    width: 20px;
  }

  &::-webkit-scrollbar-thumb {
    background: #888;
    border-radius: 5px;
  }
`;

export const AchievementShowMore = () => {

  const containerRef = useRef<HTMLDivElement>(null);

  const scrollUp = () => {
    if (containerRef.current) {
      containerRef.current.scrollTop -= 50;
    }
  };

  const scrollDown = () => {
    if (containerRef.current) {
      containerRef.current.scrollTop += 50;
    }
  };
  return (
    <div
      style={{
        position: "absolute",
        top: 0,
        right: 0,
        left: 0,
        backgroundSize: "cover",
        backgroundRepeat: "no-repeat",
        backgroundPosition: "center",
        display: 'flex',
        padding: '0 20px',
        flexDirection: 'column',
        justifyContent: 'space-between',
        boxSizing: "border-box",
        height: "896px",
        background: "#B12D2D",
      }}>
      <ChangeProfileImage />
      <h1 style={{
        backgroundColor: "#ccc",
        padding: "20px 46px",
        borderRadius: "5px",
        position: "absolute",
        top: "40%",
        left: "26%",
        fontSize: "20px",
        color: "white",
        transform: "translate(-50%, -50%)",
      }}>
        Rank name
      </h1>
      <div
        ref={containerRef}
        style={{
          backgroundColor: "#ccc",
          borderRadius: "2%",
          position: "absolute",
          width: "400px",
          height: "600px",
          left: "45%",
          top: "30%",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          overflowY: "scroll",
        }}
        >
        <ScrollableContainer style={{}}>

        <div>
          {[...Array(10)].map((_, i) => (
            <Oval
              key={i}
              width="350px"
              height="120px"
              borderRadius="20%/40%"
              border="2.5px dashed red"
              color="#B12D2D"
              marginBottom="10px"
              marginBlockStart="10px"
              marginLeft="15px"
            >
              <Square
                position="relative"
                width="100px"
                height="80px"
                borderRadius="10%"
                border="1.5px solid white"
                color="#ccc"
                marginLeft="200px"
                marginBlockStart="18px"
              />
            </Oval>
          ))}
        </div>
      </ScrollableContainer>
      </div >
    </div>
  );
};
