import React, { useRef } from 'react'
import { ChangeProfileImage } from './ChangeProfileImage'

export const AchievementShowMore = () => {

    const containerRef = useRef<HTMLDivElement>(null);

    const scrollUp = () => {
      if (containerRef.current) {
        containerRef.current.scrollTop -= 50;
      }
    };

    const scrollDown = () => {
      if (containerRef.current) {
        containerRef.current.scrollTop += 50;
      }
    };

  return (
    <div
      style={{
        position: "absolute",
        top: 0,
        right: 0,
        left: 0,
        backgroundSize: "cover",
        backgroundRepeat: "no-repeat",
        backgroundPosition: "center",
        display: 'flex',
        padding: '0 20px',
        flexDirection: 'column',
        justifyContent: 'space-between',
        boxSizing: "border-box",
        height: "896px",
        background: "#B12D2D",
      }}>
      <ChangeProfileImage />
      <h1 style={{
        backgroundColor: "#ccc",
        padding: "20px 46px",
        borderRadius: "5px",
        position: "absolute",
        top: "40%",
        left: "26%",
        fontSize: "20px",
        transform: "translate(-50%, -50%)",
      }}>
        Rank name
      </h1>
      <div
        style={{
          backgroundColor: "#ccc",
          borderRadius: "2%",
          position: "absolute",
          width: "400px",
          height: "600px",
          left: "45%",
          top: "30%",
          display: "flex",
          flexDirection: "column",
          // padding: "20px 46px",

          // justifyContent: "center", 
          alignItems: "center"
        }}
        ref={containerRef}
        style={{
          height: '200px',
          overflowY: 'scroll',
        }}
      >
        {/* Add your content here */}
        <button onClick={scrollUp}>Scroll Up</button>
        <button onClick={scrollDown}>Scroll Down</button>

      </div >
    </div>
  );
};
