import React, { useState } from 'react'
import { ChangeProfileImage } from './ChangeProfileImage'

export const AchievementShowMore = () => {
  const [showScrollButton, setShowScrollButton] = useState(false);

  const handleScroll = () => {
    const position = window.pageYOffset;
    if (position > 100) {
      setShowScrollButton(true);
    } else {
      setShowScrollButton(false);
    }
  };

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  };

  window.addEventListener("scroll", handleScroll);
  return (
    <div
      style={{
        position: "absolute",
        top: 0,
        right: 0,
        left: 0,
        backgroundSize: "cover",
        backgroundRepeat: "no-repeat",
        backgroundPosition: "center",
        display: 'flex',
        padding: '0 20px',
        flexDirection: 'column',
        justifyContent: 'space-between',
        boxSizing: "border-box",
        height: "896px",
        background: "#B12D2D",
      }}>
      <ChangeProfileImage />
      <h1 style={{
        backgroundColor: "#ccc",
        padding: "20px 46px",
        borderRadius: "5px",
        position: "absolute",
        top: "40%",
        left: "26%",
        fontSize: "20px",
        transform: "translate(-50%, -50%)",
      }}>
        Rank name
      </h1>
      <div>
        {showScrollButton && (
          <button
            onClick={scrollToTop}
            style={{
              backgroundColor: "#ccc",
              borderRadius: "2%",
              position: "absolute",
              width: "400px",
              height: "600px",
              left: "45%",
              top: "30%",
              display: "flex",
              flexDirection: "column",
              // padding: "20px 46px",

              // justifyContent: "center", 
              alignItems: "center"
            }}
          >

          </button>
        )}>
      </div >
    </div>
  );
};
